import React, {Component} from 'react';
import './App.css';

class App extends Component{
  constructor(){

    //can define state since its called once initially
    super();
    this.state={
      name: 'jophhn'
    }
    console.log('constructor');
  };
  componentWillMount(){
    if(window.innerWidth<400){
      this.setState({innerWidthValue: innerWidth});
    }
    console.log('componentWillMount');
  }
  render() {
    return (
      <div className="App">
      {this.state.name}
      {this.state.innerWidthValue}
      </div>
    );
  }
}

export default App;
